#!/usr/bin/env bash

basedir=$(
    cd $(dirname $0)
    pwd
)

function generate_local() {
    bash ${basedir}/generate-system.sh --bscChainId 02ca
    bash ${basedir}/generate-crosschain.sh --bscChainId 02ca
    bash ${basedir}/generate-relayerHub.sh --network local
    bash ${basedir}/generate-tendermintLightClient.sh --network local --initConsensusStateBytes "42696e616e63652d436861696e2d4e696c650000000000000000000000000000000000000000000229eca254b3859bffefaf85f4c95da9fbd26527766b784272789c30ec56b380b6eb96442aaab207bc59978ba3dd477690f5c5872334fc39e627723daa97e441e88ba4515150ec3182bc82593df36f8abb25a619187fcfab7e552b94e64ed2deed000000e8d4a51000"
    bash ${basedir}/generate-validatorSet.sh --initBurnRatio 1000 --network local
    bash ${basedir}/generate-systemReward.sh --network local
    bash ${basedir}/generate-slashIndicator.sh --network local
    forge build
    node ${basedir}/generate-genesis.js --chainId 714
}

function generate_QA() {
    bash ${basedir}/generate-system.sh --bscChainId 02ca
    bash ${basedir}/generate-crosschain.sh --bscChainId 02ca
    bash ${basedir}/generate-relayerHub.sh --network QA
    bash ${basedir}/generate-tendermintLightClient.sh --initConsensusStateBytes ""
    bash ${basedir}/generate-validatorSet.sh --initBurnRatio 1000 --initValidatorSetBytes ""
    forge build
    node ${basedir}/generate-genesis.js --chainId 714
}

function generate_testnet() {
    bash ${basedir}/generate-system.sh --bscChainId 0061
    bash ${basedir}/generate-crosschain.sh --bscChainId 0061
    bash ${basedir}/generate-relayerHub.sh --network testnet
    bash ${basedir}/generate-tendermintLightClient.sh --initConsensusStateBytes ""
    bash ${basedir}/generate-validatorSet.sh --initBurnRatio 1000 --initValidatorSetBytes ""
    forge build
    node ${basedir}/generate-genesis.js --chainId 97
}

function generate_mainnet() {
    bash ${basedir}/generate-system.sh --bscChainId 0038
    bash ${basedir}/generate-crosschain.sh --bscChainId 0038
    bash ${basedir}/generate-relayerHub.sh --network mainnet
    bash ${basedir}/generate-tendermintLightClient.sh --initConsensusStateBytes ""
    bash ${basedir}/generate-validatorSet.sh --initBurnRatio 1000 --initValidatorSetBytes ""
    forge build
    node ${basedir}/generate-genesis.js --chainId 56
}

CMD=$1

case ${CMD} in
local)
    echo "===== generate genesis of local ====="
    generate_local
    echo -e "\n===== end ====="
    ;;
QA)
    echo "===== generate genesis of QA ====="
    generate_QA
    echo -e "\n===== end ====="
    ;;
testnet)
    echo "===== generate genesis of testnet ====="
    generate_testnet
    echo -e "\n===== end ====="
    ;;
mainnet)
    echo "===== generate genesis of mainnet ====="
    generate_mainnet
    echo -e "\n===== end ====="
    ;;
*)
    echo "Usage: generate.sh local | QA | testnet | mainnet"
    ;;
esac
